import React, { useState } from "react";
import styled, { css } from "styled-components";
import { onCellSelect } from "../actions";

const Div = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 50px;
  width: 50px;
  border: 1px solid black;
  box-sizing: border-box;
  text-align: center;
  font-size: 20px;
  ${({ isSelected }) =>
    isSelected &&
    css`
      background: red;
    `}
`;

function Cell({ value }) {
  const [selected, setSelected] = useState(false);

  const colourCell = () => {
    if (!selected) {
      setSelected(true);
    }
  };

  return (
    <Div isSelected={selected} onClick={colourCell}>
      {value}
    </Div>
  );
}

export default Cell;
