import Model from './model';


export default (state = Model, action) => {
    switch (action.type) {
      case 'SIMPLE_ACTION':
        return {
          result: action.payload
        }
      case 'selectCell':
        return {
          ...state,
          selected: action.payload
        }
      case '':
          return {
            ...state,
            selected: action.payload
          }
     default:
      return state
    }
   }
