import React, {Component} from 'react';

class Cell extends Component {
	//validating the imput
	validImput = (i) => {
		return (i === '' || (!isNaN(i) && i.length === 1))
	}

	//what to do when the value of the cell changes
	cellValueChange = (e) => {
		const newCellValue = e.target.value;
		if (this.validImput(newCellValue)) {
			const rI = this.props.rowIndex;
			const cI = this.props.colIndex;
			this.props.onValueChange(rI, cI, newCellValue);
		}
	}

	generateCells = () => {
		//only user can modify values 
		const disabled = this.props.editable;
		const cellValue = this.props.value === '0' ? "" : this.props.value;

		//styles for the borders of the cells
		const style = {};
		const rI = this.props.rowIndex;
		const cI = this.props.colIndex;
		if (rI > 0 && (rI % 3) === 0) {
			style['borderTop'] = '2px solid black';
		}
		if (cI > 0 && (cI % 3) === 0) {
			style['borderLeft'] = '2px solid black';
		}
		if (this.props.conflict) {
			if (this.props.editable) {
				style['background'] = 'red';
			} else {
				style['border'] = '1px solid red'
			}
		}

		return (
			<td>
				<input
					className = "cell"
					style = {style}
					type = "text"
					value = {cellValue}
					disabled = {disabled}
					onChange = {this.cellValueChange}
				/>
			</td>
		);
	}
}

export default Cell;
