import React, {Component} from 'react';

class Cell extends Component {
	//what to do when the value of the cell changes



	generateCells = () => {
		//only user can modify values 
		const enabled = this.props.editable;
		const cellValue = this.props.value === '0' ? "" : this.props.value;

		//styles for the borders of the cells
		const style = {};
		const rI = this.props.rowIndex;
		const cI = this.props.colIndex;
		if (rI > 0 && (rI % 3) === 0) {
			style['borderTop'] = '2px solid black';
		}
		if (cI > 0 && (cI % 3) === 0) {
			style['borderLeft'] = '2px solid black';
		}
		if (this.props.conflict) {
			if (this.props.editable) {
				style['background'] = 'red';
			} else {
				style['border'] = '1px solid red'
			}
		}




	}
}

export default Cell;
